/* eslint-disable */
import './App.css';
import { useState } from 'react';

function App() {

  let [글제목, set글제목] = useState(['남자코트 추천', '강남 우동맛집', '파이썬독학']);
  let [따봉, set따봉] = useState([0, 0, 0]);
  let [modal, setModal] = useState(false);

  return (
    <div className="App">
      <div className="black-nav">
        <h4 style={{color : 'beige', fontsize: '16px'}}>ReactBlog</h4>
      </div>

      {/* <button onClick={() => {
        let copy = [...글제목];
        copy.sort();
        set글제목(copy);
      }}>정렬버튼</button>

      <button onClick={() => { 
        let copy = [...글제목];
        copy[0] = '여자코트 추천';
        set글제목(copy);
      }}>글수정</button> */}
      {/* <div className='list'>
        <h4> {글제목[0]}  <span onClick={ () => { set따봉(따봉 + 1) }}>👍
        </span> {따봉}</h4>
        <p>2월 17일 발행</p>
      </div>

      <div className='list'>
        <h4> { 글제목[1] }</h4>
        <p>2월 17일 발행</p>
      </div>
      <div className='list'>
        <h4 onClick={() => { setModal(!modal) }}>{ 글제목[2]}</h4>
        <p>2월 17일 발행</p>
      </div> */}

      {
        글제목.map(function (a, i){
          return (
            <div className='list' key ={i}>
               <h4 onClick={() => { setModal(!modal) }}> {글제목[i]}  
                  <span onClick={ () => { 
                    let new따봉 = [...따봉];
                    new따봉[i] += 1;
                    set따봉(new따봉);
                  }}>👍
                  </span> {따봉[i]}
                </h4>
              <p>2월 17일 발행</p>
            </div>

          )
        })
      }


      {
        // 조건식 ? 참일때 실행할 코드 : 거짓일 때 실행할 코드
        // <자식컴포넌트 작명 = {State 이름}>
        modal == true ? 
          <Modal color={'orange'} 글제목 ={글제목} 글제목변경={글제목변경}/> : null
      }
    </div>
  );
}



function Modal(props){
  return (
    <>
      <div className='modal' style={{background: props.color}}>
        <h4>{props.글제목[0]}</h4>
        <p>날짜</p>
        <p>상세내용</p>
        <button onClick={() => {props.글제목변경(['여자코트 추천', '강남 우동맛집', '파이썬독학']) }}>
          글수정</button>
      </div>
    </>
  );
}

export default App;
